import{_ as e,c as i,o as a,a as r}from"./app.5996277b.js";const v=JSON.parse('{"title":"üìö Innovation in the Reference Services of Libraries","description":"","frontmatter":{},"headers":[{"level":2,"title":"Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"My Role","slug":"my-role","link":"#my-role","children":[]},{"level":2,"title":"Project Goal","slug":"project-goal","link":"#project-goal","children":[]},{"level":2,"title":"People Involved","slug":"people-involved","link":"#people-involved","children":[{"level":3,"title":"Librarians","slug":"librarians","link":"#librarians","children":[]},{"level":3,"title":"Users","slug":"users","link":"#users","children":[]}]},{"level":2,"title":"Research","slug":"research","link":"#research","children":[]},{"level":2,"title":"Final Design","slug":"final-design","link":"#final-design","children":[]}],"relativePath":"design/library_app/index.md"}'),n={name:"design/library_app/index.md"},t=r('<p><a href="/design/index.html">‚Üê Design</a></p><br><h1 id="üìö-innovation-in-the-reference-services-of-libraries" tabindex="-1">üìö Innovation in the Reference Services of Libraries <a class="header-anchor" href="#üìö-innovation-in-the-reference-services-of-libraries" aria-hidden="true">#</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h2><p>The reference service is an activity carried out by librarians in information units which consists in supporting user in their information needs, in order to promote access, dissemination and use of information.</p><p>This project was an academic team work that resulted in a case study carried out in a UFPE library, where we developed a proposal for an information product to improve communication between the library&#39;s Reference Service and the academic community.</p><h2 id="my-role" tabindex="-1">My Role <a class="header-anchor" href="#my-role" aria-hidden="true">#</a></h2><ul><li>UI Prototyping</li></ul><h2 id="project-goal" tabindex="-1">Project Goal <a class="header-anchor" href="#project-goal" aria-hidden="true">#</a></h2><p>Know the existing reference services and propose new products or services that may help in the development of the library and in fulfilling of its mission.</p><h2 id="people-involved" tabindex="-1">People Involved <a class="header-anchor" href="#people-involved" aria-hidden="true">#</a></h2><h3 id="librarians" tabindex="-1">Librarians <a class="header-anchor" href="#librarians" aria-hidden="true">#</a></h3><p>The Library of the Education Center of the Federal University of Pernambuco is an information unit that serves the university&#39;s academic community by offering information services, the main ones being:</p><ul><li>Bibliographic materials consultation</li><li>Loan, reservation and return of materials</li><li>Cataloging at source</li><li>Guidance for standardizing academic work</li><li>Services for people with special needs or other accessibility approach</li></ul><h3 id="users" tabindex="-1">Users <a class="header-anchor" href="#users" aria-hidden="true">#</a></h3><p>As part of academic routine, users usually use library for:</p><ul><li>Reading</li><li>Internet access</li><li>Group meetings</li><li>Consultation of bibliographic materials</li><li>Loan and return of materials</li></ul><h2 id="research" tabindex="-1">Research <a class="header-anchor" href="#research" aria-hidden="true">#</a></h2><p>Analyzing the data obtained in the interview with the librarian, the team observed that:</p><p>The library does not have a channel for receiving suggestions, criticisms, complaints or compliments.</p><ul><li>45% of the users indicated that when they cannot find a book, they look for it in another library.</li></ul><p>A questionnaire was also applied to people using the information unit to find out what they do when they need to access and use bibliographic information.</p><h2 id="final-design" tabindex="-1">Final Design <a class="header-anchor" href="#final-design" aria-hidden="true">#</a></h2>',23),o=[t];function l(s,h,d,c,p,u){return a(),i("div",null,o)}const m=e(n,[["render",l]]);export{v as __pageData,m as default};
